package source.produk;public class Produk{	private int idProduk;	private String namaProduk;	private String jenisProduk;	private int stok;	private int harga;	private String namaSuplier;		public void setIdProduk(int id){		this.idProduk = id;	}		public int getIdProduk(){		return this.idProduk;	}		public void setNamaProduk(String nama){		this.namaProduk = nama;	}		public String getNamaProduk(){		return this.namaProduk;	}		public void setJenis(String jenis){		this.jenisProduk = jenis;	}		public String getJenis(){		return this.jenisProduk;	}		public void setStok(int stok){		this.stok = stok;	}		public int getStok(){		return this.stok;	}		public void setHarga(int harga){		this.harga = harga;	}		public int getHarga(){		return this.harga;	}		public void setNamaSuplier(String namaSuplier){		this.namaSuplier = namaSuplier;	}		public String getNamaSuplier(){		return this.namaSuplier;	}		public String toString(){        return this.namaProduk;    }}